import React from 'react';
// countup
import CountUp from 'react-countup';
// intersection observer
import { useInView } from 'react-intersection-observer';
// motion 
import { motion } from 'framer-motion';
// variant
import { fadeIn } from '../variants';

const About = () => {
  const {ref, inView} = useInView({
    threshhold: 0.5,
  });
  return (
    <section className='section' id='about' ref={ref}>
      <div className='container mx-auto'>
        <div>
          {/* img */}
          <div className='flex-1 bg-about bg-contain bg-no-repeat h-[640px] mix-blend-lighten bg-top'>img</div>
          {/* text */}
          <div>
            <h2>About me.</h2>
            <h3>I'm a software engineering student in university</h3>
            <p>ddfsddhdhdhdhdhdhdhshdhdfhdhdhdhhdhdhdhdhdhdhdhdhdhdhdsddsdsdsdsds</p>
            {/* stats */}
            <div className='flex gap-x-6 lg:gap-x-10 mb-12'>
              <div>
                <div className='text-[40px] font-tertiary text-gradient mb-2'>
                  {inView ? <CountUp start={0} end={4} duration={3}/> : null}
                </div>
                <div className='font-primary text-sm tracking-[2px]'>
                  Years of <br/> Experience
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
    );
};

export default About;
