import React from 'react';
// countup
import CountUp from 'react-countup';
// intersection observer
import { useInView } from 'react-intersection-observer';
// motion 
import { motion } from 'framer-motion';
// variant
import { fadeIn } from '../variants';

const About = () => {
  const {ref, inView} = useInView({
    threshhold: 0.5,
  });
  return (
    <section className='section' id='about' ref={ref}>
      <div className='container mx-auto'>
        <div className='flex flex-col gap-y-10 lg:flex-row lg:items-center lg:gap-x-20'>
          {/* img */}
          <div className='flex-1 bg-about bg-contain bg-no-repeat h-[640px] mix-blend-lighten bg-top'></div>
          {/* text */}
          <div className='flex-1'>
            <h2 className='h2 text-accent'>About me.</h2>
            <h3 className='h3 mb-4'>I'm a software engineering student in university</h3>
            <p className='mb-6'>
              ddfsddhdhdhdhdhdhdhshdhdf
              hdhdhdhhdhd hdhdhdhdhdhdh dhdhdsddsdsdsd
              sds
            </p>
            {/* stats */}
            <div className='flex gap-x-6 lg:gap-x-10 mb-12'>
              <div>
                <div className='text-[35px] font-tertiary text-gradient mb-2'>
                  {inView ? <CountUp start={0} end={3} duration={3}/> : null}
                </div>
                <div className='font-primary text-sm tracking-[2px]'>
                  Current <br/> School Year
                </div>
              </div>
              <div>
                <div className='text-[35px] font-tertiary text-gradient mb-2'>
                  {inView ? <CountUp start={0} end={22} duration={3}/> : null}
                </div>
                <div className='font-primary text-sm tracking-[2px]'>
                  Projects <br/> Completed
                </div>
              </div>
              <div>
                <div className='text-[35px] font-tertiary text-gradient mb-2'>
                  {inView ? <CountUp start={0} end={4} duration={3}/> : null}
                </div>
                <div className='font-primary text-sm tracking-[2px]'>
                  Internships: <br/> Months Completed
                </div>
              </div>
            </div>
            <div className='flex gap-x-8 items-center'>
              <button className='btn btn-lg'>Contact me</button>
              <a href='#' className='text-gradient btn-link'>
                My Portfolio
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
    );
};

export default About;
